mapper.select:
select * from member
and member_id like #{memberId} || '%'
and instr(member_nick, #{memberNick}) > 0
and member_contact = #{memberContact}
and member_point between #{minPoint} and #{maxPoint}
and member_point >= #{minPoint}
and member_point <= #{maxPoint}
and to_char(member_join, 'YYYY-MM-DD') = #{memberJoin}
and member_login is not null and member_login between to_date(#{minLoginDate} || ' ' || '00:00:00', 'YYYY-MM-DD HH24:MI:SS') and to_date(#{maxLoginDate} || ' ' || '23:59:59', 'YYYY-MM-DD HH24:MI:SS')
and member_login is not null and member_login >= to_date(#{minLoginDate} || ' ' || '00:00:00', 'YYYY-MM-DD HH24:MI:SS')
and member_login is not null and member_login <= to_date(#{maxLoginDate} || ' ' || '23:59:59', 'YYYY-MM-DD HH24:MI:SS')
and member_level in
#{memberLevel}
