mapper.insert:
insert into menu(
	menu_no, menu_name_kor, menu_name_eng, menu_type, menu_price
)
values(
	#{menuNo}, #{menuNameKor}, #{menuNameEng}, #{menuType}, #{menuPrice}
)

mapper.update:
update menu

	menu_name_kor = #{menuNameKor},
	menu_name_eng = #{menuNameEng},
	menu_type = #{menuType},
	menu_price = #{menuPrice}

where menu_no = #{menuNo}
