mapper.insert:
insert into student(
	student_id, name, korean_score, math_score, english_score
)
values(
	#{studentId},#{name},#{koreanScore}, #{mathScore}, #{englishScore}
)

mapper.update:
update student


		name = #{name},


		korean_score = #{koreanScore},


		math_score = #{mathScore},


		english_score = #{englishScore}


where student_id = #{studentId}
